(function(){function getVideoURL(){var name=document.getElementsByTagName("link")[1];var attr=name.getAttribute("rel");if(attr=="image_src"){var formatURL=document.getElementsByTagName("link")[1].getAttribute("href");}else if(attr=="canonical"){var formatURL=document.getElementsByTagName("link")[2].getAttribute("href");}else{var formatURL=document.getElementsByTagName("link")[0].getAttribute("href");}if(formatURL.indexOf("/thumbs")==-1){var embed;if(document.getElementById("specials_video_player_swf")!=null){embed=document.getElementById("specials_video_player_swf");var id=embed.getAttribute("flashvars");id=id.split("ID=")[1];id=id.split("&")[0];id="i"+id;formatURL=document.getElementById(id).getAttribute("src");}else if(document.getElementById("group_player_box_swf")!=null){embed=document.getElementById("group_player_box_swf");var id=embed.getAttribute("flashvars");id=id.split("ID=")[1];id=id.split("&")[0];img=new Array();img=document.getElementsByTagName("img");src=new Array();var j=0;for(var i=0;i<img.length;i++){if(img[i].getAttribute("src")!=null){src[j]=img[i].getAttribute("src");if(src[j].indexOf(id)!=-1){formatURL=src[j];}j++;}}}else if(document.getElementById("featuredvideo1_box_swf")!=null){embed=document.getElementById("featuredvideo1_box_swf");var id=embed.getAttribute("flashvars");id=id.split("ID=")[1];id=id.split("&")[0];id="i"+id;formatURL=document.getElementById(id).getAttribute("src");}}formatURL=formatURL.replace("/thumbs","");formatURL=formatURL.split("_");formatURL[1]=".flv";var formats=formatURL[0]+formatURL[1];return formats;}var btnDownload;var playerDiv;function checkVideo(){var videoURL=getVideoURL();videoURL=bvd_InfoExtractor.getVideoInfo(videoURL);BSMyTubeGetModule.AddUrl(videoURL,false,false);return true;}checkVideo();})();
